public class CicloDo {

 public static void main(String[] args) {
  // TODO Auto-generated method stub
  
  
  int i = 0;
  
  do {
   
   
   
   System.out.println("sto ciclando con do while" +i);
   i++;
  }
  
  while (i < 3);
    
  
  for (int j = 0; j < 4; j++) {
   
   System.out.println("sto ciclando con for" + j);
  }
 }

}
